import React from 'react';
import styled from 'styled-components'

const ContainerAdicionarTarefa = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 4% 30%;
  padding: 2%;

  border: 1px solid gray;
  box-shadow: 1px 2px gray;
`
const ContainerTarefas = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 4% 30%;
  padding: 2%;

  border: 1px solid gray;
  box-shadow: 1px 2px gray;
`

class App extends React.Component {
  state = {
    inputTarefa: '',
    inputDate: '',
    listaDeTarefas: [
      {id: 1, texto: 'limpar o quarto', date: '07/02/2022'} 
    ]
  }

  render(){
    const tarefas = this.state.listaDeTarefas.map((tarefas) => {
      return (
        <ContainerTarefas>
          <input type='checkbox'/>
          <p>
            {tarefas.texto}
          </p>
          <p>
            {tarefas.date}
          </p>
          <button onClick={() => this.deleteTarefa(tarefas.id)}> X </button>
        </ContainerTarefas>
      )
    })



    return (
      <div>
        <h1> Lista De Tarefas </h1>
        <ContainerAdicionarTarefa>
        <input type='text' placeholder='Tarefa'/>
        <input type='date'/>
        <button> Adicionar Tarefa </button>
        </ContainerAdicionarTarefa>
      </div>
    )
  }
}
export default App;
