import React from 'react';
import styled from 'styled-components'

const ContainerPrincipal = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  margin: 4% 30%;
  padding: 1%;

  border: 1px solid gray;
  box-shadow: 1px 2px gray;
`
const ContainerAdicionarTarefas = styled.div`
  display: flex;
  padding-bottom: 2%;
`

const ContainerTarefas = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  margin: 1% 30%;
  /* padding: 2%; */

  border: 1px solid gray;
  box-shadow: 1px 2px gray;
`

class App extends React.Component {
  state = {
    inputTarefa: '',
    inputDate: '',
    listaDeTarefas: [
      {id: 1, texto: 'limpar o quarto', date: '07/02/2022'} 
    ]
  }

  guardaTexto = (event) => {
    this.setState({inputTarefa: event.target.value})
  }
  guardaDate = (event) => {
    this.setState({inputDate: event.target.value})
  }

  render(){
    const tarefas = this.state.listaDeTarefas.map((tarefas) => {
      return (
        <ContainerTarefas>
          <input type='checkbox'/>
          <p>
            {tarefas.texto}
          </p>
          <p>
            {tarefas.date}
          </p>
          <button onClick={() => this.deleteTarefa(tarefas.id)}> X </button>
        </ContainerTarefas>
      )
    })



    return (
      <div>
        <ContainerPrincipal>
          <h1> Lista De Tarefas </h1>
          <ContainerAdicionarTarefas>
            <input 
              type='text' 
              placeholder='Tarefa'
              value={this.state.inputTarefa}
              onChange={this.guardaTexto}
            />
            <input 
              type='date'
              value={this.state.inputDate}
              onChange={this.guardaDate}
            />
            <button> Adicionar Tarefa </button>
          </ContainerAdicionarTarefas>
        </ContainerPrincipal>
        {tarefas}
      </div>
    )
  }
}
export default App;
